      p := tv(5);
      q := tv(6);
      r := tv(7);

      g1 := p and q and r;

      MS_E_CH_RESET <= '0';
      wait for 30 ns;
      MS_E_CH_RESET <= '1';      
      wait for 30 ns;

      -- These three signals comprise +S SET E CH UNIT SEL REG
      PS_PERCENT_OR_COML_AT <= p;
      PS_I_RING_HDL_BUS(4) <= q;
      PS_LAST_LOGIC_GATE_1 <= r;
      
