         check1(PS_E_CH_U_SEL_REG_BUS(HDL_C_BIT),not b and c,testName,"Set E Ch U Sel reg C Bit");
         check1(PS_E_CH_U_SEL_REG_NOT_BUS(HDL_C_BIT),not b and not c,testName,"Set E Ch U Sel reg NOT C Bit");
