   PS_RESET_F2_FULL_LATCH <= '0';
   check1(PS_F2_REG_WORD_SEPARATOR,'0',testName,"2G");   
   check1(MS_F2_REG_WORD_SEPARATOR,'1',testName,"2H");
   PS_SET_F2_REG_DELAYED <= '1';
   wait for 30 ns;  -- Should set again 
